Scripts.OnMouseDown:
if ( self:IsEnabled() == "1" ) then
	self.normalTexture:SetPoint("CENTER", 1, -1);
	self.highlightTexture:SetPoint("CENTER", 1, -1);
end

Scripts.OnClick:
PlaySound("igMainMenuOptionCheckBoxOn");
local index = self:GetParent():GetID();
MoveCharacter(index, index - 1);

PlaySound("igMainMenuOptionCheckBoxOn");
local index = self:GetParent():GetID();
MoveCharacter(index, index + 1);
